import React, { useState, useEffect} from 'react';
import RecipeForm from './RecipeForm';
import Recipe from './Recipe';
import firebase from 'firebase';

function RecipeList() {

  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const textareaStorage = firebase.firestore().collection("recipeCollection");

  // const saveData =
  // textareaStorage.onSnapshot((querySnapshot) =>{
  //   const items = [];
  //   querySnapshot.forEach((doc)=>{
  //     items.push(doc.data())
  //   })
  //   setRecipes(items);
  //   setLoading(false);
  // })
  // useEffect(() => {
  //   saveData();
  // }, [])
  useEffect(() => {
    const getPostsFromFirebase = [];
    const subscriber = textareaStorage
      .onSnapshot((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          getPostsFromFirebase.push({
            ...doc.data(),
            key: doc.id
          });
        });
        setRecipes(getPostsFromFirebase);
        setLoading(false);
      });
      return () => subscriber();
  },[loading]);

  if (loading) {
    return <h1>loading firebase data...</h1>;
  }

  const addRecipe = recipe => {
    textareaStorage
    .doc()
    .set(recipe)
    .catch((err) => {
      console.error(err);
    });
    // if (!recipe.text || /^\s*$/.test(recipe.text)) {
    //   return;
    // }
    // textareaStorage.doc().set(recipe);

    // const newRecipes = [recipe, ...recipes];

    // setRecipes(newRecipes);

  };


  const updateRecipe = (recipeId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }

    setRecipes(prev => prev.map(item => (item.id === recipeId ? newValue : item)));
  };

  const removeRecipe = id => {
    const removedArr = [...recipes].filter(recipe => recipe.id !== id);

    setRecipes(removedArr);
  };

  return (
    <main className="main">
      <RecipeForm onSubmit={addRecipe} />
      <Recipe
        recipes={recipes}
        removeRecipe={removeRecipe}
        updateRecipe={updateRecipe}
      />
    </main>
  );
}

export default RecipeList;
