    setRecipes(prev => prev.map(item => (item.id === recipeId ? newValue : item)));
  };

  const removeRecipe = id => {
    const removedArr = [...recipes].filter(recipe => recipe.id !== id);

    setRecipes(removedArr);
  };

  return (
    <main className="main">
